mapper.select:
select type,id
from practice

    submit_id=#{gradeSechVO.stuId}  and type=#{type}

        and subject_id=#{gradeSechVO.subjectId}


        and create_time >= #{gradeSechVO.begin}


        and create_time <= #{gradeSechVO.end}

select grade
from practice

    submit_id=#{gradeSechVO.stuId}  and type=#{type}

        and subject_id=#{gradeSechVO.subjectId}


        and create_time >= #{gradeSechVO.begin}


        and create_time <= #{gradeSechVO.end}

select create_time
from practice

    submit_id=#{stuId}

        and subject_id=#{subjectId}


        and create_time >= #{begin}


        and create_time <= #{end}


order by create_time
